    render(){
        const Formdata=styled.div`
         background-color: aquamarine;
        height: auto;
        width:300px;
        margin: auto;
        padding: 5px;
    }`
    const buttons= {
    width:"40%",
    marginLeft:"10px"
    }
        
    
        return(
            <Formdata>
            <form>
                <label htmlFor="name">FirstName</label>
                <input type="text" name="name" id="name" placeholder="Enter your name"/>
                <label htmlFor="lastname">LastName</label>
                <input type="text" name="lastname" id="lastname" placeholder="Enter last name"/>
                <label htmlFor="fathername">FatherName</label>
                <input type="text" name="fathername" id="fathername" placeholder="Enter father name"/>
                <label htmlFor="mothername">MotherName</label>
                <input type="text" name="mothername" id="mothername" placeholder="Enter mother name"/><br/>
                <label htmlFor="birthdate">Date of Birth</label><br/>
                <input type="date" name="birthdate" id="birthdate"/>
                <label htmlFor="email">Email</label>
                <input type="email" name="email" id="email"/>
                <lable htmlFor="state">State</lable>
                <select style={{width:"90%"}}>
                <option>Select state</option>
                {indianStates.map(state,index)}
                </select>
                    

                <button type="reset" style={buttons}>Reset</button>
                <button type="submit" style={buttons}>Submit</button>
                <lable></lable>
            </form>
            </Formdata>
        )
    }
}

export default Form;
