import './App.css';
// import Sachin16 from './Sachin16';
// import Persun from './component/Persun';
import axios from 'axios';
import Cl from './Cl'
import { Component } from 'react';
import { render } from '@testing-library/react';

class App extends Component{
 constructor(props){
  super(props);
  this.props=props;
  this.state={
    classs: [
      { 
        id: 1,
        name: 'sakshi Gupte',
        age: 26
      },
      { 
        id: 2,
        name: 'rupali prabha',
        age: 25
      },
      { 
        id: 3,
        name: 'Naveen',
        age: 45
      },
      { 
        id: 4,
        name: 'hemant',
        age:  34
      },
      {
        id:5,
        name: 'rahul',
        age: 50
      }
      ],
      isShow : true,

  };


 }
     toggleClick(){

        this.setState({isShow: !this.state.isShow})


      }
      removeclick=(parsonsindex)=>{
      alert(parsonsindex);
      }
    render(){
         let parsons;
      if(this.state.isShow){

      parsons = this.state.classs.map((t,index)=>{
        return(
         < Cl
          key={index}
          name={t.name} 
          id={t.id}
          age={t.age}
           remove={this.removeclick.bind(index)}
            />
        )

      }

    )}else{
      parsons =" ";
    }



      return(
      <div className='data'>
        <button onClick={this.toggleClick.bind(this)}> Toggle</button>
      {this.state.isShow === true ? parsons : " " } 
       </div>
      )


    }
  }


export default App;
