import './App.css';
import axios from 'axios';
import Ragistration from './Ragistration';
import Greet from './Greet';
import { Component } from 'react';
import { render } from '@testing-library/react';
import Contenar from './component/Contenar';
import firebase from 'firebase/compat/app';
import { v4 as uuidv4 } from 'uuid';
const firebaseConfig = {
  apiKey: "AIzaSyDRSNxhbAmEcnFWBDF9VtzYbA1V0FmRjx0",
  authDomain: "sachinmahajan-cbcce.firebaseapp.com",
  databaseURL: "https://sachinmahajan-cbcce-default-rtdb.firebaseio.com",
  projectId: "sachinmahajan-cbcce",
  storageBucket: "sachinmahajan-cbcce.firebasestorage.app",
  messagingSenderId: "146261052015",
  appId: "1:146261052015:web:7f95bbc92e6c36a5f65b9a"
};
if(firebase.apps.length < 0){
firebase.initializeApp(firebaseConfig);
}

 export default class App extends Component{
  constructor(props){
    super(props);
    this.state={
      id: uuidv4(),
      isNextpage: false,
      name: null,
      email: null,
      password: null,
      showpassword: false

    };
  }
  submitHendler=(event)=>{

    let name=event.target.name.value;
    let email=event.target.email.value;
    let password=event.target.password.value;

    this.setState({name,email,password,isNextpage: true})

    const database= firebase.database();
    function writeUserData(userId, name, email, imageUrl) {
  firebase.database().ref('users/' + userId).set({
    username: name,
    email: email,
    profile_picture : imageUrl
  });
}
  };
  onclicpassword =()=>{
    this.setState({showpassword : ! this.state.showpassword})
  }

  render(){

    return(
      <div>
      {
         this.state.isNextpage ?
        <Greet name={this.state.name} email={this.state.email}/> 
        : < Ragistration 
        submit={this.submitHendler}
        showpassword={this.state.showpassword}
        click={this.onclicpassword}
        /> 
      }
      </div>
    )
  }
}














