import { Component } from "react";
class Cl  extends Component
{
    constructor(props){
    super(props);
    this.props=props;
    this.state={
        name: "sachin",
    }
    
    }
    buttonclick(){
       console.log(this.state)
       this.setState({name: "raju"}),()=>{
        console.log(this.state.name);
       }
    }

    render(){
        return(
         <>
         <i>{this.props.name}sachin mahajan</i>
         <button onClick={()=>this.buttonclick}>Click</button>
         </>
        )
    }
}      
export default Cl
