import './App.css';
import { Component } from 'react';
import { getDatabase, ref, set } from 'firebase/database';
import { app } from "./Firebas"

import Cl from './Apna-app/Cl';
import Contenar from './component/Contenar';
import { BrowserRouter,Routes,Route } from 'react-router-dom';
import Shopping from './Apna-app/Shopping'
import Home from './Apna-app/Home';
import Contact from './Apna-app/Contact';

const db = getDatabase(app); 
 export default class App extends Component{
  constructor(props){
    super(props);
    

  }
 
render(){
   const putdata=()=>{
    set(ref(db , "users/sachin"),{
      id:1,
      name:"sachin",
      age:23,

    })
  }
  return(
    <div>
      <Cl/>
      
      <div>
      <Routes>
         <Route path='' element={<Home submit={putdata}/>}/>
        <Route path='/Shopping' element={<Shopping/>}/>
        <Route path='/Contact' element={<Contact/>}/>
      </Routes>
    </div>
  </div>
  )
}






 }
