import React, {Component} from "react";
import './form.css'
import styled from "styled-components";

class Form extends Component{
  
    
    render(){
        const Formdata=styled.div`
         background-color: aquamarine;
        height: auto;
        width:300px;
        margin: auto;
        padding: 5px;
    }`
    const buttons= {
    width:"40%"
    }
        
    
        return(
            <Formdata>
            <form>
                <label htmlFor="name">FirstName</label>
                <input type="text" name="name" id="name"/>
                <label htmlFor="lastname">LastName</label>
                <input type="text" name="lastname" id="lastname"/>
                <label htmlFor="fathername">FatherName</label>
                <input type="text" name="fathername" id="fathername"/>
                <label htmlFor="mothername">MotherName</label>
                <input type="text" name="mothername" id="mothername"/><br/>
                <button type="reset" style={buttons}>Reset</button>
                <button type="submit" style={buttons}>Submit</button>
                <lable></lable>
            </form>
            </Formdata>
        )
    }
}

export default Form;
