import './App.css';
// import Sachin16 from './Sachin16';
// import Persun from './component/Persun';
import axios from 'axios';
import Cl from './Cl'
import { Component } from 'react';
import { render } from '@testing-library/react';

class App extends Component{
  constructor(props){
    super(props);
    this.props=props;
    this.state={
        parsun :[ {name: "sachin",
        age:55,
        },
        {name:"banti",
        age: 56,
        },
        {name: "pravin",
        age: 44,
        }
    ],
    isShow: true,
}
}
toggless(){
    this.setState.apply({isShow:!this.state.isShow});
  }

render(){
    let porsons =this.state.parsun.map((p,index)=>{
        return(
            <Cl kye={index} name={p.name} age={p.age} />
        ) })


  return(
         <div>   
                <button type='button' onClick={this.toggless.bind(this)}>toggle</button>
                {this.state.isShow === true ? porsons : "" }
        </div>
    )
}}

export default App;
