import React, {Component} from "react";
import './form.css'
import styled from "styled-components";

class Form extends Component{
  
    
    render(){
        const Formdata=styled.div`
         background-color: aquamarine;
        height: auto;
        width:400px;
        margin: auto;
        padding: 5px;
    }`
    const indianStates = [
  "Andhra Pradesh", "Arunachal Pradesh", "Assam", "Bihar", "Chhattisgarh",
  "Goa", "Gujarat", "Haryana", "Himachal Pradesh", "Jharkhand",
  "Karnataka", "Kerala", "Madhya Pradesh", "Maharashtra", "Manipur",
  "Meghalaya", "Mizoram", "Nagaland", "Odisha", "Punjab",
  "Rajasthan", "Sikkim", "Tamil Nadu", "Telangana", "Tripura",
  "Uttar Pradesh", "Uttarakhand", "West Bengal", "Andaman and Nicobar Islands",
  "Chandigarh", "Dadra and Nagar Haveli and Daman and Diu", "Delhi",
  "Jammu and Kashmir", "Ladakh", "Lakshadweep", "Puducherry"
];

    const buttons= {
    width:"40%",
    marginLeft:"10px"
    }
        
    
        return(
            <Formdata>
            <form>
                <label htmlFor="name">FirstName</label>
                <input type="text" name="name" id="name" placeholder="Enter your name"/>
                <label htmlFor="lastname">LastName</label>
                <input type="text" name="lastname" id="lastname" placeholder="Enter last name"/>
                <label htmlFor="fathername">FatherName</label>
                <input type="text" name="fathername" id="fathername" placeholder="Enter father name"/>
                <label htmlFor="mothername">MotherName</label>
                <input type="text" name="mothername" id="mothername" placeholder="Enter mother name"/><br/>
                <label htmlFor="birthdate">Date of Birth</label><br/>
                <input type="date" name="birthdate" id="birthdate"/>
                <label htmlFor="email">Email</label>
                <input type="email" name="email" id="email"/>
                <lable htmlFor="state">State</lable><br/>
                <select style={{width:"90%"}} name="state">
                <option>Select state</option>
                  {indianStates.map((state, index) => (
                    <option key={index} value={state}>{state}</option>
                     ))}

                </select>
                <lable htmlFor="gender">Gender</lable><br/>
                <input type="redio"></input>
                    

                <button type="reset" style={buttons}>Reset</button>
                <button type="submit" style={buttons}>Submit</button>
                
            </form>
            </Formdata>
        )
    }
}

export default Form;
